              var daynum = Math.floor((dateObj.getTime() - newYear.getTime() - (dateObj.getTimezoneOffset() - newYear.getTimezoneOffset()) * 60000) / 86400000) + 1;
              var weeknum;
              //if the year starts before the middle of a week
              if (day < 4) {
                weeknum = Math.floor((daynum + day - 1) / 7) + 1;
                if (weeknum > 52) {
                  var nYear = new Date(dateObj.getFullYear() + 1, 0, 1);
                  var nday = nYear.getDay() - dowOffset;
                  nday = nday >= 0 ? nday : nday + 7;
                  /*if the next year starts before the middle of
                    the week, it is week #1 of that year*/
                  weeknum = nday < 4 ? 1 : 53;
                }
              } else {
                weeknum = Math.floor((daynum + day - 1) / 7);
              }
              return weeknum;

              //var dayOfYear = date.dayOfYear(date.dateTime) - 1;
              //var week = date.dayOfWeek - (dayOfYear % 7);
